import React, { useContext } from 'react'
import styles from './styles.module.css'

export const ExampleComponent = ({ text }) => {
  return <div className={styles.test}>Example Component: {text}</div>
}

// PROVIDER

const FeatureFlagContext = React.createContext([])

export const FeatureFlagProvider = ({ children }) => {
  const flagsRetrieved = { flags: ['A', 'B', 'C'] }
  return (
    <FeatureFlagContext.Provider value={flagsRetrieved}>
      {children}
    </FeatureFlagContext.Provider>
  )
}

export const TestComponent = (props) => {
  return <div>Test Component</div>
}

// FEATURE FLAG COMPONENT
export const FeatureFlag = ({ flagName = '', children }) => {
  const context = useContext(FeatureFlagContext)
  console.log('CONTEXT', context, flagName)

  // if (!flags.includes(flagName)) return null

  return children
}
