import React from 'react'
import styles from './styles.module.css'

export const TestComponent = () => <div>Test</div>

export const ExampleComponent = ({ text }) => {
  return <div className={styles.test}>Example Component: {text}</div>
}

// HIGHER ORDER COMPONENT...

export const withFeatureFlags = (Component) => {
  const WithFeatureFlags = (props) => {
    const flags = ['test1', 'test2', 'test3']

    return <Component flags={flags} {...props} />
  }

  return WithFeatureFlags
}

// FEATURE FLAG COMPONENT
export const FeatureFlag = ({ flags, flagName, children }) => {
  if (!flags.includes(flagName)) return null

  return children
}
